// isWindowsDriveLetter returns true if c is a valid Windows drive letter.
func isWindowsDriveLetter(c byte) bool {
	return (c >= 'A' && c <= 'Z') || (c >= 'a' && c <= 'z')
}

// isAbsolutePath checks if a path is absolute on any platform.
// This detects both Unix-style (/path) and Windows-style (C:\path) absolute paths
// regardless of the current OS. This is important for cross-platform validation
// of invkfiles - a path like /etc/passwd should be detected as absolute even
// when validating on Windows.
func isAbsolutePath(path string) bool {
	if path == "" {
		return false
	}
	// Check for Unix-style absolute path (starts with /)
	if path[0] == '/' {
		return true
	}
	// Check for Windows-style absolute path (C:\ or C:/)
	if len(path) >= 3 && isWindowsDriveLetter(path[0]) && path[1] == ':' &&
		(path[2] == '\\' || path[2] == '/') {
		return true
	}
	return false
}
